import React, { useState, useEffect } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, ReferenceLine } from 'recharts';
import Papa from 'papaparse';
import _ from 'lodash';

const COLORS = ['#8884d8', '#82ca9d', '#ffc658', '#ffc658'];

const formatDate = (dateStr) => {
  if (!dateStr) return '';
  try {
    // Check if the date is already in YYYY-MM format
    if (dateStr.includes('-')) {
      const [year, month] = dateStr.split('-');
      return `${year}-${String(month).padStart(2, '0')}`;
    }
    // Handle the YYYYmMM format
    const year = dateStr.substring(0, 4);
    const month = dateStr.substring(5);
    return `${year}-${String(month).padStart(2, '0')}`;
  } catch (error) {
    console.error('Error formatting date:', error);
    return dateStr; // Return original string if parsing fails
  }
};

const convertGradDate = (gradDate) => {
  if (!gradDate) return '';
  const [month, year] = gradDate.split('-');
  const monthNum = new Date(Date.parse(month + " 1, 2000")).getMonth() + 1;
  return `20${year}m${monthNum}`;
};

const LoginScreen = ({ onLogin }) => {
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const hash = (str) => {
    let hash = 0;
    for (let i = 0; i < str.length; i++) {
      const char = str.charCodeAt(i);
      hash = ((hash << 5) - hash) + char;
      hash = hash & hash;
    }
    return hash;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (hash(password) === -1621563955) {
      onLogin();
    } else {
      setError('Incorrect password');
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50">
      <div className="max-w-md w-full space-y-8 p-8 bg-white rounded-lg shadow">
        <h2 className="text-2xl font-bold text-center">TCI Dashboard Login</h2>
        <form onSubmit={handleSubmit} className="space-y-6">
          <div>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter password"
              className="w-full p-2 border rounded focus:ring-2 focus:ring-blue-500"
            />
          </div>
          {error && <p className="text-red-500 text-sm">{error}</p>}
          <button
            type="submit"
            className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600"
          >
            Login
          </button>
        </form>
      </div>
    </div>
  );
};

const CityChart = ({ data, city, selectedVars }) => {
  const cityData = data[0] || {};
  const impDate = cityData.impdate;
  const gradDate = cityData.graduationdate;

  // Sort data by date
  const sortedData = _.sortBy(data, 'reportingdate');

  return (
    <div className="mb-8 border rounded p-4">
      <div className="mb-8">
        <h3 className="text-lg font-medium mb-2">{city}</h3>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
          <div>
            <span className="font-medium">NCU per 1000:</span> {cityData.NCU_per_1000}
          </div>
          <div>
            <span className="font-medium">NCU:</span> {cityData.NCU}
          </div>
          <div>
            <span className="font-medium">NCU Final Period:</span> {cityData.NCU_final_period}
          </div>
          <div>
            <span className="font-medium">P Portmanteau:</span> {cityData.p_portmenteau?.toFixed(3)}
          </div>
          <div>
            <span className="font-medium">Integration:</span> {cityData.integration}
          </div>
          <div>
            <span className="font-medium">Analysis:</span> {cityData.analysis}
          </div>
          <div>
            <span className="font-medium">LRT P-value:</span> {cityData.lrt_pvalue?.toFixed(3)}
          </div>
          <div>
            <span className="font-medium">Ramp P-value:</span> {cityData.ramp_pvalue?.toFixed(3)}
          </div>
          <div className="col-span-2">
            <span className="font-medium">Model:</span>{' '}
            <span className="bg-gray-100 px-2 py-1 rounded">AR: {cityData.ar}</span>{' '}
            <span className="bg-gray-100 px-2 py-1 rounded">MA: {cityData.ma}</span>
          </div>
        </div>
      </div>
      <div style={{ height: '400px' }} className="mt-2">
        <ResponsiveContainer>
          <LineChart data={sortedData}>
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis 
              dataKey="reportingdate" 
              tickFormatter={formatDate}
              interval="preserveStartEnd"
              angle={45}
              textAnchor="start"
              height={80}
              padding={{ left: 20, right: 20 }}
              tick={{
                fontSize: 12,
                dy: 8,
                dx: 8
              }}
            />
            <YAxis />
            <Tooltip 
              labelFormatter={formatDate}
              formatter={(value) => (typeof value === 'number' ? value.toFixed(2) : value)}
            />
            <Legend />
            <ReferenceLine 
              x={impDate} 
              stroke="red" 
              strokeDasharray="3 3" 
              label={{ 
                value: 'imp date', 
                angle: 90,
                position: 'insideTopRight',
                fill: 'red',
                fontSize: 9,
                offset: 5,
                backgroundColor: 'white',
                padding: 2,
                dy: 50
              }} 
            />
            <ReferenceLine 
              x={convertGradDate(gradDate)} 
              stroke="blue" 
              strokeDasharray="3 3" 
              label={{ 
                value: 'grad date', 
                angle: 90,
                position: 'insideTopRight',
                fill: 'blue',
                fontSize: 9,
                offset: 5,
                backgroundColor: 'white',
                padding: 2,
                dy: 50
              }} 
            />
            {selectedVars.totalreportingsdp_imp && (
              <Line type="monotone" dataKey="totalreportingsdp_imp" stroke={COLORS[0]} strokeWidth={2} name="Total Reporting SDP (Imp)" dot={false} />
            )}
            {selectedVars.nac_wraadj_total_imp && (
              <Line type="monotone" dataKey="nac_wraadj_total_imp" stroke={COLORS[1]} strokeWidth={2} name="NAC Wrap Adj Total (Imp)" dot={false} />
            )}
            {selectedVars.nac_wraadj_int && (
              <Line type="monotone" dataKey="nac_wraadj_int" stroke={COLORS[2]} strokeWidth={2} name="NAC Wrap Adj Int" dot={false} />
            )}
            {selectedVars.nac_wraadj_noint && (
              <Line type="monotone" dataKey="nac_wraadj_noint" stroke={COLORS[3]} strokeDasharray="5 5" strokeWidth={2} name="NAC Wrap Adj No Int" dot={false} />
            )}
          </LineChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
};

const DashboardContent = ({ onLogout }) => {
  const [data, setData] = useState([]);
  const [countries, setCountries] = useState([]);
  const [cities, setCities] = useState([]);
  const [selectedCountry, setSelectedCountry] = useState('');
  const [selectedCities, setSelectedCities] = useState([]);
  const [selectedVars, setSelectedVars] = useState({
    totalreportingsdp_imp: true,
    nac_wraadj_total_imp: true,
    nac_wraadj_int: true,
    nac_wraadj_noint: true
  });

  useEffect(() => {
    const loadData = async () => {
      try {
        const SHEET_ID = '1ute_A9t0CBvWwwvMPGwz6OSGeQO6_qV8';
        const SHEET_GID = '972210733';
        const url = `https://docs.google.com/spreadsheets/d/${SHEET_ID}/export?format=csv&gid=${SHEET_GID}`;

        const response = await fetch(url);

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const text = await response.text();
        const result = Papa.parse(text, {
          header: true,
          dynamicTyping: true,
          skipEmptyLines: true
        });

        setData(result.data);
        const uniqueCountries = _.uniq(result.data.map(row => row.country)).sort();
        setCountries(uniqueCountries);

      } catch (error) {
        console.error('Error loading data:', error);
      }
    };

    loadData();
  }, []);

  useEffect(() => {
    if (selectedCountry) {
      const countryCities = _.uniq(
        data
          .filter(row => row.country === selectedCountry)
          .map(row => row.city)
      ).sort();
      setCities(countryCities);
      setSelectedCities([]);
    }
  }, [selectedCountry, data]);

  const handleCountryChange = (e) => {
    setSelectedCountry(e.target.value);
  };

  const handleCityChange = (e) => {
    const city = e.target.value;
    setSelectedCities(prev => 
      prev.includes(city) 
        ? prev.filter(c => c !== city)
        : [...prev, city]
    );
  };

  const handleVarChange = (variable) => {
    setSelectedVars(prev => ({
      ...prev,
      [variable]: !prev[variable]
    }));
  };

  return (
    <div className="p-4 max-w-6xl mx-auto">
      <div className="flex justify-between items-center mb-4">
        <h1 className="text-2xl font-bold">Dashboard - TCI</h1>
        <button 
          onClick={onLogout}
          className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
        >
          Logout
        </button>
      </div>

      <div className="mb-6 grid grid-cols-1 md:grid-cols-2 gap-4">
        <div>
          <label className="block text-sm font-medium mb-1">Select Country:</label>
          <select 
            value={selectedCountry} 
            onChange={handleCountryChange}
            className="w-full p-2 border rounded"
          >
            <option value="">Select a country...</option>
            {countries.map(country => (
              <option key={country} value={country}>{country}</option>
            ))}
          </select>
        </div>

        <div>
          <label className="block text-sm font-medium mb-1">Select Variables:</label>
          <div className="space-y-2">
            {Object.keys(selectedVars).map((variable, idx) => (
              <label key={variable} className="flex items-center">
                <input
                  type="checkbox"
                  checked={selectedVars[variable]}
                  onChange={() => handleVarChange(variable)}
                  className="mr-2"
                />
                <span style={{ color: COLORS[idx] }}>{variable}</span>
              </label>
            ))}
          </div>
        </div>
      </div>

      {selectedCountry && (
        <div className="mb-6">
          <label className="block text-sm font-medium mb-1">Select Cities:</label>
          <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-2">
            <label className="flex items-center font-medium">
              <input
                type="checkbox"
                checked={selectedCities.length === cities.length}
                onChange={(e) => {
                  if (e.target.checked) {
                    setSelectedCities(cities);
                  } else {
                    setSelectedCities([]);
                  }
                }}
                className="mr-2"
              />
              Select All
            </label>
            {cities.map(city => (
              <label key={city} className="flex items-center">
                <input
                  type="checkbox"
                  value={city}
                  checked={selectedCities.includes(city)}
                  onChange={handleCityChange}
                  className="mr-2"
                />
                {city}
              </label>
            ))}
          </div>
        </div>
      )}

      <div className="mt-8">
        {selectedCities.map(city => {
          const cityData = data
            .filter(row => row.city === city)
            .map(row => ({
              ...row,
              reportingdate: row.reportingdate // Keep the original date format
            }));

          return (
            <CityChart 
              key={city}
              data={cityData}
              city={city}
              selectedVars={selectedVars}
            />
          );
        })}
      </div>

      {selectedCountry && selectedCities.length === 0 && (
        <p>Please select one or more cities</p>
      )}
    </div>
  );
};

const Dashboard = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  if (!isLoggedIn) {
    return <LoginScreen onLogin={() => setIsLoggedIn(true)} />;
  }

  return <DashboardContent onLogout={() => setIsLoggedIn(false)} />;
};

export default Dashboard;
